custom_styles:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: "Hiragino Kaku Gothic ProN", "Hiragino Sans", "メイリオ", Meiryo, sans-serif;
    line-height: 1.9;
    color: #333;
    font-size: 16px;
    letter-spacing: 0.04em;
}

.container {
    max-width: 800px;
    margin: 0 auto;
    padding: 0 15px;
}

/* ヘッダー画像部分 */
.header-image {
    background: linear-gradient(135deg, #f0fdf9 0%, #ecfdf5 50%, #f0fdf9 100%);
    padding: 30px 20px 20px;
    text-align: center;
    color: #1a1a1a;
}

.top-tags {
    display: flex;
    justify-content: center;
    gap: 15px;
    margin-bottom: 15px;
}

.tag {
    background: #ffffff;
    color: #166534;
    padding: 8px 18px;
    border-radius: 6px;
    font-weight: bold;
    font-size: 14px;
    border: 2px solid #d1fae5;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
}

.main-copy {
    font-size: 16px;
    margin-bottom: 15px;
    font-weight: bold;
}

.main-title-small {
    background: white;
    color: #166534;
    padding: 10px 20px;
    border-radius: 40px;
    font-size: 16px;
    margin-bottom: 10px;
    display: inline-block;
    font-weight: bold;
    box-shadow: 0 4px 12px rgba(6, 199, 85, 0.15);
    position: relative;
    border: 2px solid #a7f3d0;
}

.main-title-small:after {
    content: '';
    position: absolute;
    bottom: -8px;
    left: 50%;
    transform: translateX(-50%);
    width: 0;
    height: 0;
    border-left: 12px solid transparent;
    border-right: 12px solid transparent;
    border-top: 12px solid white;
}

.main-title {
    font-size: 48px;
    font-weight: 800;
    margin-bottom: 15px;
    line-height: 1.3;
    letter-spacing: -0.03em;
}

.threads-text {
    color: #000;
}

.line-text {
    color: #06C755;
}

.results-banner {
    color: #1a1a1a;
    padding: 15px 20px 0;
    margin-top: 15px;
    font-size: 14px;
}

.results-banner h3 {
    font-size: 15px;
    margin-bottom: 5px;
    font-weight: bold;
}

.results-banner p {
    font-size: 14px;
    line-height: 1.6;
    font-weight: bold;
}

/* CTAボタン */
.cta-section {
    text-align: center;
    padding: 25px 20px;
    background: #f8f9fa;
}

.cta-text {
    font-size: 16px;
    margin-bottom: 15px;
    font-weight: bold;
}

.cta-button {
    display: inline-block;
    background: linear-gradient(135deg, #00B900 0%, #06C755 50%, #00A84F 100%);
    color: white;
    padding: 15px 40px;
    font-size: 18px;
    font-weight: bold;
    text-decoration: none;
    border-radius: 50px;
    transition: all 0.3s;
    box-shadow: 0 4px 15px rgba(6, 199, 85, 0.4);
    animation: bounce 2s ease-in-out infinite;
}

.cta-button:hover {
    box-shadow: 0 6px 20px rgba(6, 199, 85, 0.6);
    transform: translateY(-2px);
    animation: bounce 2s ease-in-out infinite;
}

/* セクション共通 */
.section {
    padding: 40px 20px;
}

.section-title {
    font-size: 32px;
    text-align: center;
    margin-bottom: 25px;
    font-weight: 800;
    color: #333;
    letter-spacing: -0.03em;
    line-height: 1.3;
}

/* 背景色バリエーション */
.bg-white {
    background: #FFFFFF;
}

.bg-light-gray {
    background: #f0fdf4;
}

.bg-light-purple {
    background: #f0fdf4;
}

.bg-light-green {
    background: #f0fdf4;
}

/* 波線divider */
.wave-divider {
    height: 50px;
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 120" preserveAspectRatio="none"><path d="M321.39,56.44c58-10.79,114.16-30.13,172-41.86,82.39-16.72,168.19-17.73,250.45-.39C823.78,31,906.67,72,985.66,92.83c70.05,18.48,146.53,26.09,214.34,3V0H0V27.35A600.21,600.21,0,0,0,321.39,56.44Z" fill="%23f0fdf4"/></svg>') repeat-x;
    background-size: 100% 100%;
}

.wave-divider-white {
    height: 50px;
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 120" preserveAspectRatio="none"><path d="M321.39,56.44c58-10.79,114.16-30.13,172-41.86,82.39-16.72,168.19-17.73,250.45-.39C823.78,31,906.67,72,985.66,92.83c70.05,18.48,146.53,26.09,214.34,3V0H0V27.35A600.21,600.21,0,0,0,321.39,56.44Z" fill="%23FFFFFF"/></svg>') repeat-x;
    background-size: 100% 100%;
}

/* 数字の円形ビジュアル化 */
.number-circle {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    width: 120px;
    height: 120px;
    background: #ffffff;
    border-radius: 50%;
    color: #06C755;
    font-size: 32px;
    font-weight: 800;
    margin: 10px;
    box-shadow: 0 10px 30px rgba(6, 199, 85, 0.2);
    border: 3px solid #dcfce7;
}

.number-label {
    display: block;
    text-align: center;
    font-size: 14px;
    color: #666;
    margin-top: 5px;
}

.stats-container {
    display: flex;
    flex-wrap: nowrap; /* 必ず横並びにする */
    justify-content: center;
    align-items: flex-start;
    gap: 20px;
    margin: 30px 0;
    overflow-x: auto; /* 小さい画面でもスクロール可能に */
}

.stat-item {
    text-align: center;
    flex-shrink: 0; /* アイテムが縮まないようにする */
    min-width: 120px; /* 最小幅を確保 */
}

/* 上下に跳ねるアニメーション */
@keyframes bounce {
    0%, 100% {
        transform: translateY(0);
    }
    50% {
        transform: translateY(-10px);
    }
}

/* スクリーンショット風の実績表示 */
.screenshot-mockup {
    background: white;
    border-radius: 20px;
    padding: 20px;
    box-shadow: 0 15px 40px rgba(6, 199, 85, 0.15);
    border: 8px solid #d1fae5;
    margin: 30px auto;
    max-width: 350px;
}

.screenshot-header {
    display: flex;
    align-items: center;
    gap: 6px;
    margin-bottom: 15px;
}

.screenshot-dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: #ddd;
}

.screenshot-dot:nth-child(1) { background: #ff5f56; }
.screenshot-dot:nth-child(2) { background: #ffbd2e; }
.screenshot-dot:nth-child(3) { background: #27c93f; }

.screenshot-content {
    background: #f8f9fa;
    padding: 20px;
    border-radius: 10px;
    font-size: 14px;
    line-height: 1.6;
}

/* 受講生の実績セクション */
.student-results {
    background: linear-gradient(135deg, #f0fdf4 0%, #dcfce7 50%, #f0fdf4 100%);
    color: #1a1a1a;
    padding: 50px 20px;
}

.student-card {
    background: white;
    color: #333;
    border-radius: 15px;
    padding: 25px;
    margin-bottom: 20px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
}

.student-name {
    font-size: 22px;
    font-weight: 800;
    color: #059669;
    margin-bottom: 10px;
    text-align: center;
}

.student-achievement {
    font-size: 18px;
    font-weight: bold;
    color: #1a1a1a;
    margin-bottom: 15px;
    line-height: 1.6;
    text-align: center;
}

.student-video-link {
    display: inline-block;
    background: linear-gradient(135deg, #00B900 0%, #06C755 50%, #00A84F 100%);
    color: white;
    padding: 10px 25px;
    border-radius: 25px;
    text-decoration: none;
    font-size: 14px;
    font-weight: bold;
    transition: all 0.3s;
}

.student-video-link:hover {
    transform: scale(1.05);
    box-shadow: 0 5px 15px rgba(6, 199, 85, 0.4);
}

.student-results .section-title {
    color: #1a1a1a;
    margin-bottom: 15px;
}

.student-intro {
    text-align: center;
    font-size: 18px;
    margin-bottom: 40px;
    line-height: 1.8;
}

/* YouTubeレスポンシブ埋め込み - YouTube標準サイズ 1280x720px (16:9) */
.video-wrapper {
    position: relative;
    padding-bottom: 56.25%; /* 16:9 aspect ratio (720/1280 = 0.5625 = 56.25%) - YouTube標準サイズ */
    height: 0;
    overflow: hidden;
    border-radius: 10px;
    margin-top: 15px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.15);
}

.video-wrapper iframe {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border: none;
}

/* スクロールFade-inアニメーション */
.fade-in {
    opacity: 0;
    transform: translateY(30px);
    transition: opacity 0.8s ease-out, transform 0.8s ease-out;
}

.fade-in.visible {
    opacity: 1;
    transform: translateY(0);
}

/* 遅延アニメーション */
.fade-in-delay-1 {
    transition-delay: 0.1s;
}

.fade-in-delay-2 {
    transition-delay: 0.2s;
}

.fade-in-delay-3 {
    transition-delay: 0.3s;
}

.fade-in-delay-4 {
    transition-delay: 0.4s;
}

/* グラデーションアニメーション */
@keyframes gradient-shift {
    0% {
        background-position: 0% 50%;
    }
    50% {
        background-position: 100% 50%;
    }
    100% {
        background-position: 0% 50%;
    }
}

.animated-gradient {
    background-size: 400% 400%;
    animation: gradient-shift 15s ease infinite;
}

/* パララックス用の装飾要素 */
.parallax-decoration {
    position: absolute;
    border-radius: 50%;
    opacity: 0.1;
    pointer-events: none;
}

.decoration-circle-1 {
    width: 300px;
    height: 300px;
    background: linear-gradient(135deg, #ecfdf5 0%, #d1fae5 100%);
    top: -100px;
    right: -100px;
}

.decoration-circle-2 {
    width: 200px;
    height: 200px;
    background: linear-gradient(135deg, #d1fae5 0%, #ecfdf5 100%);
    bottom: -50px;
    left: -50px;
}

/* 3大特典セクション */
.benefits {
    background: white;
}

.benefit-item {
    background: #f8f9fa;
    padding: 20px;
    margin-bottom: 20px;
    border-radius: 10px;
    border-left: 5px solid #10b981;
    box-shadow: 0 8px 20px rgba(6, 199, 85, 0.15), 0 1px 3px rgba(0, 0, 0, 0.05);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.benefit-item:hover {
    box-shadow: 0 15px 35px rgba(6, 199, 85, 0.25), 0 5px 15px rgba(0, 0, 0, 0.08);
    transform: translateY(-4px);
    background: #ffffff;
}

.benefit-item:hover .benefit-title {
    transform: scale(1.02);
    transition: transform 0.3s ease;
}

.benefit-title {
    font-size: 18px;
    font-weight: bold;
    margin-bottom: 12px;
    color: #1a1a1a;
}

.benefit-content ul {
    list-style: none;
    padding-left: 0;
    font-size: 14px;
}

.benefit-content li {
    padding: 6px 0;
    padding-left: 20px;
    position: relative;
}

.benefit-content li:before {
    content: "■";
    position: absolute;
    left: 0;
    color: #10b981;
    font-size: 12px;
}

.benefit-details {
    margin-top: 15px;
    padding: 15px;
    background: white;
    border-radius: 8px;
}

.benefit-details h4 {
    font-size: 15px;
    margin-bottom: 8px;
    color: #333;
}

.benefit-details ul {
    list-style: none;
    padding-left: 0;
    font-size: 13px;
}

.benefit-details ul li {
    padding: 4px 0;
    padding-left: 20px;
    position: relative;
}

.benefit-details ul li:before {
    content: "・";
    position: absolute;
    left: 0;
    color: #10b981;
}

.benefit-details p {
    font-size: 14px;
    margin-bottom: 10px;
}

/* プロフィールセクション */
.profile {
    background: #f8f9fa;
}

.profile-content {
    display: flex;
    gap: 30px;
    align-items: flex-start;
    flex-wrap: wrap;
}

.profile-image {
    flex: 0 0 200px;
}

.profile-image img {
    width: 100%;
    border-radius: 10px;
}

.profile-text {
    flex: 1;
    min-width: 300px;
}

.profile-name {
    font-size: 22px;
    font-weight: bold;
    margin-bottom: 8px;
}

.profile-role {
    font-size: 16px;
    color: #1a1a1a;
    margin-bottom: 15px;
    font-weight: bold;
}

.profile-achievements {
    margin-top: 15px;
}

.profile-achievements ul {
    list-style: none;
    font-size: 14px;
}

.profile-achievements li {
    padding: 6px 0;
    padding-left: 20px;
    position: relative;
}

.profile-achievements li:before {
    content: "●";
    position: absolute;
    left: 0;
    color: #10b981;
    font-size: 12px;
}

.profile-achievements h4 {
    font-size: 15px;
    margin-bottom: 8px;
}

/* 問題提起セクション */
.problem {
    background: white;
}

.checklist {
    background: #fff3cd;
    padding: 20px;
    border-radius: 10px;
    margin: 20px 0;
}

.checklist ul {
    list-style: none;
}

.checklist li {
    padding: 8px 0;
    padding-left: 25px;
    position: relative;
    font-size: 15px;
}

.checklist li:before {
    content: "✓";
    position: absolute;
    left: 0;
    color: #ff6b6b;
    font-weight: bold;
    font-size: 18px;
}

.highlight-box {
    background: linear-gradient(135deg, #dcfce7 0%, #bbf7d0 100%);
    color: #166534;
    padding: 20px;
    border-radius: 10px;
    margin: 20px 0;
    text-align: center;
    font-size: 16px;
    font-weight: bold;
    line-height: 1.6;
    border: 2px solid #86efac;
}

.solution-box {
    background: linear-gradient(135deg, #dcfce7 0%, #bbf7d0 100%);
    color: #166534;
    padding: 30px 20px;
    border-radius: 10px;
    margin: 20px 0;
    border: 2px solid #86efac;
}

.solution-box h3 {
    font-size: 18px;
    margin-bottom: 15px;
    text-align: center;
}

.solution-box p {
    font-size: 15px;
}

.steps {
    background: white;
    color: #333;
    padding: 20px;
    border-radius: 10px;
    margin-top: 15px;
}

.steps h4 {
    font-size: 17px;
    margin-bottom: 12px;
    color: #1a1a1a;
}

.steps ol {
    padding-left: 25px;
    font-size: 15px;
}

.steps ol li {
    padding: 8px 0;
}

.steps p {
    font-size: 14px;
}

.results-list {
    margin-top: 15px;
    padding: 15px;
    background: rgba(255, 255, 255, 0.2);
    border-radius: 10px;
}

.results-list ul {
    list-style: none;
}

.results-list li {
    padding: 6px 0;
    padding-left: 20px;
    position: relative;
    font-size: 15px;
}

.results-list li:before {
    content: "✓";
    position: absolute;
    left: 0;
    font-weight: bold;
}

.results-list p {
    font-size: 16px;
}

.emphasis-text {
    text-align: center;
    font-size: 18px;
    font-weight: bold;
    margin: 15px 0;
}

/* Threadsの理由セクション */
.reasons {
    background: #f8f9fa;
}

.reason-item {
    background: white;
    padding: 20px;
    margin-bottom: 15px;
    border-radius: 10px;
    box-shadow: 0 8px 20px rgba(6, 199, 85, 0.12), 0 1px 3px rgba(0, 0, 0, 0.05);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.reason-item:hover {
    box-shadow: 0 15px 35px rgba(6, 199, 85, 0.2), 0 5px 15px rgba(0, 0, 0, 0.08);
    transform: translateY(-4px);
    background: #ffffff;
}

.reason-item:hover .reason-title {
    color: #1a1a1a;
    transform: scale(1.02);
    transition: all 0.3s ease;
}

.reason-number {
    display: inline-block;
    background: linear-gradient(135deg, #d1fae5 0%, #a7f3d0 100%);
    color: #166534;
    padding: 4px 12px;
    border-radius: 20px;
    font-weight: bold;
    margin-bottom: 8px;
    font-size: 13px;
}

.reason-title {
    font-size: 17px;
    font-weight: bold;
    margin-bottom: 8px;
}

.reason-item p {
    font-size: 14px;
}

/* フォロワー数セクション */
.follower-myth {
    background: white;
    text-align: center;
}

.warning-box {
    background: #fff3cd;
    padding: 20px;
    border-radius: 10px;
    margin: 20px 0;
    border: 3px solid #ffc107;
}

.warning-box h3 {
    font-size: 18px;
    margin-bottom: 12px;
    color: #333;
}

.warning-box p {
    font-size: 15px;
}

/* FAQセクション */
.faq {
    background: #f8f9fa;
}

.faq-item {
    background: white;
    padding: 20px;
    margin-bottom: 15px;
    border-radius: 10px;
    box-shadow: 0 8px 20px rgba(6, 199, 85, 0.12), 0 1px 3px rgba(0, 0, 0, 0.05);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.faq-item:hover {
    box-shadow: 0 15px 35px rgba(6, 199, 85, 0.2), 0 5px 15px rgba(0, 0, 0, 0.08);
    transform: translateY(-4px);
}

.faq-question {
    font-size: 16px;
    font-weight: bold;
    margin-bottom: 10px;
    color: #1a1a1a;
}

.faq-answer {
    font-size: 14px;
    line-height: 1.7;
}

/* クロージングセクション */
.closing {
    background: white;
    text-align: center;
}

.closing-text {
    font-size: 15px;
    line-height: 1.8;
    margin-bottom: 15px;
}

.final-message {
    background: linear-gradient(135deg, #dcfce7 0%, #bbf7d0 100%);
    color: #166534;
    padding: 30px 20px;
    border-radius: 10px;
    margin: 20px 0;
    font-size: 16px;
    line-height: 1.7;
    border: 2px solid #86efac;
}

.final-message p {
    font-size: 16px;
}

/* レスポンシブ */
@media (max-width: 768px) {
    body {
        font-size: 15px;
    }

    .section {
        padding: 30px 15px;
    }

    .section-title {
        font-size: 28px;
        margin-bottom: 20px;
    }

    .main-title {
        font-size: 38px;
    }

    .number-circle {
        width: 90px;
        height: 90px;
        font-size: 22px;
    }

    .stats-container {
        gap: 10px; /* スマホでは間隔を狭める */
    }

    .stat-item {
        min-width: 90px; /* スマホでも横並びを維持 */
    }

    .number-label {
        font-size: 12px;
    }

    .student-name {
        font-size: 20px;
    }

    .student-achievement {
        font-size: 16px;
    }

    .student-intro {
        font-size: 16px;
    }

    .top-tags {
        gap: 8px;
        flex-wrap: nowrap;
    }

    .tag {
        font-size: 12px;
        padding: 6px 12px;
        white-space: nowrap;
    }

    .main-copy {
        font-size: 14px;
    }

    .main-title-small {
        font-size: 14px;
        padding: 8px 16px;
    }

    .profile-content {
        flex-direction: column;
    }

    .profile-image {
        flex: 0 0 auto;
        max-width: 150px;
        margin: 0 auto 20px;
    }

    .profile-name {
        font-size: 18px;
    }

    .profile-role {
        font-size: 14px;
    }

    .benefit-item {
        padding: 15px;
        margin-bottom: 15px;
    }

    .benefit-title {
        font-size: 16px;
    }

    .benefit-content ul {
        font-size: 13px;
    }

    .benefit-details {
        padding: 12px;
    }

    .benefit-details h4 {
        font-size: 14px;
    }

    .benefit-details ul {
        font-size: 12px;
    }

    .cta-button {
        padding: 12px 30px;
        font-size: 16px;
    }

    .cta-text {
        font-size: 14px;
    }

    .checklist {
        padding: 15px;
    }

    .checklist li {
        font-size: 13px;
        padding-left: 20px;
    }

    .highlight-box {
        padding: 15px;
        font-size: 14px;
    }

    .solution-box {
        padding: 20px 15px;
    }

    .solution-box h3 {
        font-size: 18px;
    }

    .steps {
        padding: 15px;
    }

    .steps h4 {
        font-size: 15px;
    }

    .steps ol {
        font-size: 13px;
        padding-left: 20px;
    }

    .reason-item {
        padding: 15px;
    }

    .reason-title {
        font-size: 15px;
    }

    .reason-item p {
        font-size: 13px;
    }

    .faq-item {
        padding: 15px;
    }

    .faq-question {
        font-size: 14px;
    }

    .faq-answer {
        font-size: 13px;
    }

    .warning-box {
        padding: 15px;
    }

    .warning-box h3 {
        font-size: 16px;
    }

    .closing-text {
        font-size: 13px;
    }

    .final-message {
        padding: 20px 15px;
        font-size: 14px;
    }
}

@media (max-width: 480px) {
    .number-circle {
        width: 55px;
        height: 55px;
        font-size: 14px;
        margin: 2px;
        border: 2px solid #dcfce7;
    }

    .stats-container {
        gap: 2px;
        padding: 0 3px;
    }

    .stat-item {
        min-width: 55px;
    }

    .number-label {
        font-size: 10px;
    }

    .top-tags {
        gap: 6px;
        flex-wrap: nowrap;
    }

    .tag {
        font-size: 11px;
        padding: 6px 10px;
        white-space: nowrap;
    }

    .main-title {
        font-size: 22px;
    }
}

